$(function(){
	$("#my_dialog").dialog("close");
	$("#my_dialog").bgiframe();
	String.prototype.trim=function(){
		return this.replace(/(^\s*)|(\s*$)/g, "");
	};
	//flushZqdmSelect();
	flushPsdxNameSelect();
	$("#psdxName").change(function() {
		var selectedOption = $(this).children('option:selected');
		var value = selectedOption.val();
		var a = value.split("/");
		$("#psdxAccount").val(a[0]);
		$("#psdxAccountId").val(a[1]);
		$("#psdxBankNo").val(a[2]);
	});
});

function getRow(tableId, index) {
	if (typeof(index) != "number")
		return null;
	if (index < 0)
		return null;
	var rows = $('#'+tableId).datagrid('getRows');
	if (index >= rows.length)
		return null;
	return rows[index];
}

function flushPsdxNameSelect() {
	var args = {
			"flag"        :"pre_PlacingObjAccountName",
			"securitiesId":"2543279436361870"};
	$.ajax({
		type: "POST",
		url: m_strContextPath+"action?EnquiryPageParamsAction=1",
		data: args,
		dataType:'json',
		success: function(data){
			var options = '<option value="">--请选择--</option>';
			var i = 0;
			for (i = 0; i < data.length; i++) {
				var row = data[i];
				options = options+'<option value="'+row.key+'">'+row.value+'</option>';
			}
			$("#psdxName").html(options);
		},
		error:function(msg){
		}
	});
}

function deleteSelectedRow() {
	var grid = $('#test');
	var row = grid.datagrid('getSelected');
	if (row == null)
		return;
	var index = grid.datagrid('getRowIndex', row);
	grid.datagrid('deleteRow', index);
}

function addRow(psdxAccount, psdxName, psdxPrice, psdxQuantity, 
		        psdxAccountId, psdxBankNo, lockupPeriod) {
	var grid = $('#test');
	grid.datagrid('insertRow',{
		index: 0,
		row: {
			a: psdxBankNo,
			b: psdxAccount,
			c: psdxName,
			d: psdxPrice,
			e: psdxQuantity,
			f: lockupPeriod,
			psdxAccountId: psdxAccountId
		}
	});
}

function isNumber(str) {
	var i = 0;
	var c = 0;
	var j = 0;
	for (i = 0; i < str.length; i++) {
		c = str.charAt(i);
		if (c != '.') {
			if (!(c >= '0' && c <= '9')) {
				return false;
			}
		} else {
			for (j = i+1; j < str.length; j++) {
				c = str.charAt(j);
				if (!(c >= '0' && c <= '9')) {
					return false;
				}
			}
			break;
		}
	}
	return true;
}

/**
 * 是否正整数（不包括0）
 * @returns {Boolean}
 */
function isInteger(str){
	return !(parseInt(str) == 'NaN' || parseInt(str) < 0 || str.indexOf(".")>0);
}

function tip(msg) {
	var m = "<div align='center'><br><pre>"+msg+"</pre></div>";
	$("#my_dialog").dialog({
		title: "提示",
		content: m,
		collapsible: false,
		minimizable: false,
		maximizable: false,
		width: 400,
		height: 150,
		buttons: [{
			text: '确定',
			iconCls: 'icon-ok',
			handler: function() {
				$('#my_dialog').dialog('close'); 
			}
		}]
	});
}

function insertRow() {
	var psdxAccountId = $("#psdxAccountId").val();
	var psdxBankNo = $("#psdxBankNo").val();
	var psdxAccount = $("#psdxAccount").val();
	if (psdxAccount=="") {
		tip("请先选择“配售对象名称”。");
		return;
	}
	var psdxName = $("#psdxName").children('option:selected').text();
	if (psdxName=="") {
		tip("请先选择“配售对象名称”。");
		return;
	}
	var psdxPrice = $("#psdxPrice").val();
	if (psdxPrice=="") {
		tip("请先填写“拟申购价格”。");
		return;
	}
	if (!isNumber(psdxPrice)) {
		tip("“拟申购价格”只能是数字。");
		return;
	}
	var p = parseFloat(psdxPrice);
	if (p <= 0) {
		tip("“拟申购价格”只能是非负数。");
		return;
	}
	var psdxQuantity = $("#psdxQuantity").val();
	if (psdxQuantity=="") {
		tip("请先填写“拟申购数量”。");
		return;
	}
	if (!isNumber(psdxQuantity)) {
		tip("“拟申购数量”只能是数字。");
		return;
	}
	var q = parseFloat(psdxQuantity);
	if (q <= 0) {
		tip("“拟申购数量”只能是非负数。");
		return;
	}
	var lockupPeriod = $("#lockupPeriod").val();
	if (lockupPeriod!="") {
		if (!isNumber(lockupPeriod)) {
			tip("“自愿锁定期”只能是数字。");
			return;
		}
		if (!isInteger(lockupPeriod)) {
			tip("“自愿锁定期”只能是正整数。");
			return;
		}
	}
	addRow(psdxAccount, 
	       psdxName, 
	       psdxPrice, 
	       psdxQuantity, 
	       psdxAccountId, 
	       psdxBankNo, 
	       lockupPeriod);
}

function submit(xml) {
	var args = {"__DATA":xdEncode(xml)};
	$.ajax({
		type: "POST",
		url: m_strContextPath+"action?PreEnquiryAction=1",
		contentType: "application/x-www-form-urlencoded; charset=utf-8",
		data: args,
		dataType:'html',
		success: function(data){
			if (data == "SUCCESS") {
				tip("操作成功。");
				return;
			} else {
				tip(data);
				return;
			}
		},
		error:function(msg){
			tip("操作出错。");
			return;
		}
	});
}

function HTMLEncode(text){
	if ( typeof( text ) != "string" )
		text = text.toString();
	text = text.replace(
		/&/g, "＆").replace(
		/"/g, "＂").replace(
		/</g, "＜").replace(
		/>/g, "＞").replace(
		/'/g, "＇");
	return text ;
}

function save() {
	var securitiesId = $("#SECURITIES_ID").val();
	var grid = $("#test"); 
	var rows = grid.datagrid('getRows');
	if (rows.length == 0) {
		tip("请至少录入一条“询价明细”。");
		return;
	}
	var offerBase = $("#OFFER_BASE").val();
	if (offerBase.length == 0) {
		tip("请填写“报价依据”。");
		return;
	}
	offerBase = HTMLEncode(offerBase);
	var operator = $("#OPERATOR").val();
	if (operator.length == 0) {
		tip("请填写“经办人”。");
		return;
	}
	operator = HTMLEncode(operator);
	var contactNumber = $("#CONTACT_NUMBER").val();
	if (contactNumber.length == 0) {
		tip("请填写“联系电话”。");
		return;
	}
	contactNumber = HTMLEncode(contactNumber);
	var mobilePhone = $("#MOBILE_PHONE").val();
	if (mobilePhone.length == 0) {
		tip("请填写“手机”。");
		return;
	}
	mobilePhone = HTMLEncode(mobilePhone);
	var objEval = $("#OBJ_EVAL").val();
	if (objEval.length==0) {
		tip("请选择“客观性评价”。");
		return;
	}
	var ratEval = $("#RAT_EVAL").val();
	if (ratEval.length==0) {
		tip("请选择“合理性评价”。");
		return;
	}
	var genEval = $("#GEN_EVAL").val();
	if (genEval.length==0) {
		tip("请选择“总体评价”。");
		return;
	}
	var payBail = $("#payBail").val();
	if (payBail.length==0) {
		tip("请选择“是否向主承销商缴纳保证金”。");
		return;
	}

	var xml = "";
	xml += "<OFFER>\n";
	xml += "<SECURITIES_ID>" + securitiesId + "</SECURITIES_ID>\n";
	xml += "<OFFER_BASE>" + offerBase + "</OFFER_BASE>\n";
	xml += "<OPERATOR>" + operator + "</OPERATOR>\n";
	xml += "<CONTACT_NUMBER>" + contactNumber + "</CONTACT_NUMBER>\n";
	xml += "<MOBILE_PHONE>" + mobilePhone + "</MOBILE_PHONE>\n";
	xml += "<OBJ_EVAL>" + objEval + "</OBJ_EVAL>\n";
	xml += "<RAT_EVAL>" + ratEval + "</RAT_EVAL>\n";
	xml += "<GEN_EVAL>" + genEval + "</GEN_EVAL>\n";
	xml += "<PAY_BAIL>" + payBail + "</PAY_BAIL>\n";
	xml += "<DETAIL_LIST>\n";	
	for (var i = 0; i < rows.length; i++) {
		xml += "<DETAIL>\n";
		var row = rows[i];
		xml += "<ACCOUNT>" + row.b + "</ACCOUNT>\n";
		xml += "<PRICE>" + row.d + "</PRICE>\n";
		xml += "<VOLUME>" + row.e + "</VOLUME>\n";
		xml += "<LOCKUP_PERIOD>" + row.f + "</LOCKUP_PERIOD>\n";
		xml += "</DETAIL>\n";
	}
	xml += "</DETAIL_LIST>\n";
	xml += "</OFFER>";
	submit(xml);
}

